nrqlQueries.query:
FROM DatabricksPipelineSummary 
SELECT latest(runningPipelineCount) AS 'Pipelines'
WHERE databricksWorkspaceName = {{workspace}}

FROM DatabricksPipelineSummary 
SELECT latest(failedPipelineCount) AS 'Pipelines'
WHERE databricksWorkspaceName = {{workspace}}

FROM DatabricksPipelineSummary 
SELECT latest(idlePipelineCount) AS 'Pipelines'
WHERE databricksWorkspaceName = {{workspace}}

FROM DatabricksPipelineSummary 
SELECT latest(runningUpdateCount) AS 'Updates'
WHERE databricksWorkspaceName = {{workspace}}

FROM DatabricksPipelineSummary 
SELECT latest(waitingForResourcesUpdateCount) AS 'Updates'
WHERE databricksWorkspaceName = {{workspace}}

FROM DatabricksPipelineSummary 
SELECT latest(settingUpTablesUpdateCount) AS 'Updates'
WHERE databricksWorkspaceName = {{workspace}}
